import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Todo from './Todo';
const App = () => {

    let [todo, setTodo] = useState("");
    let [todos, setTodos] = useState([]);
    const createTodo = (event) => {
        setTodo(event.target.value);
    }

    const saveTodo = () => {
        setTodos(todos.concat(todo));
        setTodo("");
    }

    const markTodo = (id) => {
        console.log(id);
        var str = todos[id];
        var result = str.strike();

        console.log(result);
        setTodos((oldList) => {
            return oldList.filter((value, index, arr) => {
                return  id === index ? arr.result : value;
            });
        })
    }

    return (
        <div className="main_div">
            <div className="center_div">
                <br />
                <h1>Todo List</h1>
                <br />
                <input type='text' placeholder='Add a todo' value={todo} onChange={createTodo} />
                <Button className="newBtn" onClick={saveTodo}>
                    <AddIcon />
                </Button>
                <br />
                <ol>
                    {
                        todos.map((value, index) => {
                            return <Todo text={value} id={index} onSelect={markTodo} />;
                        })
                    }
                </ol>
                <br />
             </div>
        </div>
    );
}

export default App;
