import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';
import Todo from './Todo';
const App = () => {

    let [todo, setTodo] = useState({
        text: "",
        done: false
    });
    let [todos, setTodos] = useState([]);

    const createTodo = (event) => {
        setTodo(() => {
            return {
                text: event.target.value,
                done: false
            }
        });
    }

    const saveTodo = () => {
        setTodos(todos.concat(todo));
        setTodo(() => {
            return {
                text: "",
                done: false
            }
        });
    }

    const markTodo = async (id) => {
        console.log(id);
 
        await setTodos((oldList) => {
            // return oldList.filter((value, index) => {
            //     return  index !== id;
            // });
            oldList[id].done = true;
            console.log(oldList);
            return oldList;
        })
    }

    return (
        <div className="main_div">
            <div className="center_div">
                <br />
                <h1>Todo List</h1>
                <br />
                <input type='text' placeholder='Add a todo' value={todo.text} onChange={createTodo} />
                <Button className="newBtn" onClick={saveTodo}>
                    <AddIcon />
                </Button>
                <br />
                <ol>
                    {
                        todos.map((value, index) => {
                            return <Todo text={value.text} done={value.done} key={index} id={index} onSelect={markTodo} />;
                        })
                    }
                </ol>
                <br />
             </div>
        </div>
    );
}

export default App;
