import React, { useState } from 'react';

const App = () => {

    let [user, setUser] = useState({
        uname : "",
        upass : "",
        finalName : "",
        finalPass : ""
    });

    const onSubmits = (event) => {
        event.preventDefault();
        alert("Form Submitted");

    }

    const inputEvent = (event) => {
        const value = event.target.value;
        const name = event.target.name;
        console.log(value);
        console.log(name);
        setUser((prev) => {
            if(name === 'ufname') {
                return {
                    uname: value,
                    upass: prev.upass
                }          
            } else if(name === 'ufpass') {
                return {
                    uname: prev.uname,
                    upass: value
                }
            }
        })
    }

    return (
        <>
            {/* <div className="main_div"> */}
                <form onSubmit={onSubmits}>
            <div>
                <h1> Hello {user.finalName} {user.finalPass}</h1>
                <input 
                    type='text' 
                    placeholder="Enter Your Name"
                    name="ufname"
                    onChange={inputEvent} 
                    value={user.uname}
                />
                <input 
                    type='text' 
                    placeholder="Enter Your Password"
                    name="ufpass"
                    onChange={inputEvent} 
                    value={user.upass}
                />
                <button type="submit"> Submit </button>
            </div>
            </form>
            {/* </div> */}
        </>
    );
}

export default App;
